import styled from "styled-components";

export const StyledButton = styled.button`
  border: 0;
  border-radius: 5px;
  padding: 5px;
  margin-top: 2%;
  background-color: darkblue;
  color: white;
  font-weight: bold;
  cursor: pointer;
  transition: 1000ms;
  :hover {
    border: 1px solid white;
  }
`;
