import React from "react";
// import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import { useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
export const Login = () => {
  interface data {
    email: string;
    password: string;
  }
  // const formSchema = yup.object().shape({
  //   email: yup
  //     .string()
  //     .required("Por favor, informe seu email")
  //     .email("Email invaĺido, tente novamente"),
  //   password: yup.string().required("Por favor, digite sua senha"),
  // });

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<data>({
    // resolver: yupResolver(formSchema)
  });

  const history = useHistory();

  const onSubmitFunction = (data: data) => {
    history.push("/dashboard");
    console.log(data);
  };
  return (
    <form onSubmit={handleSubmit(onSubmitFunction)}>
      <input placeholder="email" {...register("email")}></input>
      <span>{errors.email?.message}</span>
      <input placeholder="senha" {...register("password")}></input>
      <span>{errors.password?.message}</span>
      <button type="submit">enviar</button>
    </form>
  );
};
